func TestGoldenmd5(t *testing.T) {
	for i := 0; i < len(goldenmd5); i++ {
		// fmt.Println("Test MD5:", i)
		g := goldenmd5[i]
		b := Md5(bytes.NewBufferString(g.in))
		s := fmt.Sprintf("%x", b.Bytes())
		if s != g.out {
			t.Fatalf("Sum function: MD5(%s) = %s want %s", g.in, s, g.out)
		}
	}
}

func benchmarkSizeMd5(b *testing.B, size int) {
	b.SetBytes(int64(size))
	for i := 0; i < b.N; i++ {
		Md5(bytes.NewBuffer(buf[:size]))
	}
}

func BenchmarkHash8BytesMd5(b *testing.B) {
	benchmarkSizeMd5(b, 8)
}

func BenchmarkHash320BytesMd5(b *testing.B) {
	benchmarkSizeMd5(b, 320)
}

func BenchmarkHash1KMd5(b *testing.B) {
	benchmarkSizeMd5(b, 1024)
}

func BenchmarkHash8KMd5(b *testing.B) {
	benchmarkSizeMd5(b, 8192)
}
